mapper.select:
SELECT u.uid, u.password, u.username, u.nickname, u.email, u.phone, u.avatar, u.status, GROUP_CONCAT( r.role_name ) AS roleNames, GROUP_CONCAT( r.rid ) AS roleIds, u.create_time FROM pet_user u LEFT JOIN pet_user_role pur ON u.uid = pur.uid LEFT JOIN pet_role r ON pur.rid = r.rid
u.deleted = 0
AND ( u.username LIKE CONCAT('%',#{queryParams.keywords},'%') OR u.nick_name LIKE CONCAT('%',#{queryParams.keywords},'%') OR u.phone LIKE CONCAT('%',#{queryParams.keywords},'%') )
AND u.status = #{queryParams.status}
AND u.create_time >= #{queryParams.startTime}
AND u.create_time <= #{queryParams.endTime}
GROUP BY u.uid
